using UnityEngine;using System.Collections;public class PatrolState : FSMState{    public PatrolState(Transform[] wp)     {         waypoints = wp;        stateID = FSMStateID.Patrolling;        curRotSpeed = 6.0f;        curSpeed = 80.0f;    }    public override void Reason(Transform player, AIController npc)    {        //Check the distance with player tank        //When the distance is near, transition to chase state        if (Vector3.Distance(npc.role.transform.position, player.position) <= chaseDistance)        {            Debug.Log("Switch to Chase State");            npc.SetTransition(Transition.SawPlayer);        }    }    public override void Act(Transform player, AIController npc)    {        //Find another random patrol point if the current point is reached		        if (Vector3.Distance(npc.role.transform.position, destPos) <= arriveDistance)        {            Debug.Log("Reached to the destination point\ncalculating the next point");            FindNextPoint();        }        //Rotate to the target point        Quaternion targetRotation = Quaternion.LookRotation(destPos - npc.role.transform.position);        npc.role.transform.rotation = Quaternion.Slerp(npc.role.transform.rotation, targetRotation, Time.deltaTime * curRotSpeed);        //Go Forward        		CharacterController controller = npc.role.GetComponent<CharacterController>();		controller.SimpleMove(npc.role.transform.forward * Time.deltaTime * curSpeed);		//Animation animComponent = npc.role.GetComponent<Animation>();		//animComponent.CrossFade("Walk");    }}